import React, { useState } from "react";
import { jwtDecode } from "jwt-decode";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userRole, setUserRole] = useState(null); // Almacena el rol del usuario
  const [errorMessage, setErrorMessage] = useState(null); // Almacena mensajes de error

  const handleLogin = async () => {
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nombreUsuario: username,
          contrasena: password
        })
      });

      const data = await response.json();
      if (response.ok) {
        // Guardar token en localStorage
        localStorage.setItem("jwtToken", data.token);

        // Decodificar el token para extraer el rol
        const decodedToken = jwtDecode(data.token);
        setUserRole(decodedToken.role);

        // Resetear estado de error y ocultar login
        setErrorMessage(null);
        setShowLogin(false);
      } else {
        throw new Error(data.message || "Error al iniciar sesión");
      }
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("jwtToken");
    setUserRole(null);
    setShowLogin(true);
  };

  // Renderizar el dashboard basado en el rol
  if (userRole === 'ADMIN') {
    return <AdminDashboard />;
  } else if (userRole === 'USER') {
    return <UserDashboard />;
  }

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">PROPSUITE</h1>
        <hr className="divider" />
        {!showLogin ? (
          <>
            <h2 className="subtitle">¡Gestión de edificios al alcance de tus manos!</h2>
            <div className="button-container">
              <button className="custom-button" onClick={() => setShowLogin(true)}>Iniciar Sesión</button>
              <button className="custom-button">Registrarse</button>
            </div>
          </>
        ) : (
          <div className="form-horizontal">
            <div className="input-group">
              <label>Usuario</label>
              <input
                type="text"
                placeholder="Ingrese su usuario"
                value={username}
                onChange={e => setUsername(e.target.value)}
              />
            </div>
            <div className="input-group">
              <label>Contraseña</label>
              <input
                type="password"
                placeholder="Ingrese su contraseña"
                value={password}
                onChange={e => setPassword(e.target.value)}
              />
            </div>
            <div className="button-container">
              <button className="custom-button" onClick={handleLogin}>Iniciar Sesión</button>
              <button className="custom-button" onClick={() => setShowLogin(false)}>Volver</button>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
