import React, { useState } from "react";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false); // Controla qué pantalla mostrar

  const handleLoginClick = () => {
    setShowLogin(true); // Muestra solo el formulario de inicio de sesión
  };

  const handleBackClick = () => {
    setShowLogin(false); // Regresa a la pantalla principal
  };

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">CONSORTIUOROOOOOOM</h1>
        <hr className="divider" />
        {!showLogin ? (
          <>
            <h2 className="subtitle">
              ¡Gestión de edificios al alcance de tus manos!
            </h2>
            <div className="button-container">
              <button className="custom-button" onClick={handleLoginClick}>
                Iniciar Sesión
              </button>
              <button className="custom-button">Registrarse</button>
            </div>
          </>
        ) : (
          <div className="login-box">
            <h2 className="login-title">Iniciar Sesión</h2>
            <div className="input-group">
              <label>Usuario</label>
              <input type="text" placeholder="Ingrese su usuario" />
            </div>
            <div className="input-group">
              <label>Contraseña</label>
              <input type="password" placeholder="Ingrese su contraseña" />
            </div>
            <button className="custom-button">Iniciar Sesión</button>
            <button className="back-button" onClick={handleBackClick}>
              VOLVER
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
