import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const navigate = useNavigate();

  const handleLoginClick = () => {
    setShowLogin(true);
  };

  const handleRegisterClick = () => {
    navigate("/register"); // Navegar a la página de registro
  };

  const handleBackClick = () => {
    setShowLogin(false);
  };

  const handleLoginSubmit = () => {
    navigate("/login"); // Navegar a la página de inicio de sesión
  };

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">PROPSUITE</h1>
        <hr className="divider" />
        {!showLogin ? (
          <>
            <h2 className="subtitle">
              ¡Gestión de edificios al alcance de tus manos!
            </h2>
            <div className="button-container">
              <button className="custom-button" onClick={handleLoginClick}>
                Iniciar Sesión
              </button>
              <button className="custom-button" onClick={handleRegisterClick}>
                Registrarse
              </button>
            </div>
          </>
        ) : (
          <div className="form-horizontal">
            <div className="input-group">
              <label>Usuario</label>
              <input type="text" placeholder="Ingrese su usuario" />
            </div>
            <div className="input-group">
              <label>Contraseña</label>
              <input type="password" placeholder="Ingrese su contraseña" />
            </div>
            <div className="button-container">
              <button className="custom-button" onClick={handleLoginSubmit}>
                Iniciar Sesión
              </button>
              <button className="custom-button" onClick={handleBackClick}>
                Volver
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}


export default App;
