import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [documentoPersona, setDocumentoPersona] = useState("");
  const [message, setMessage] = useState(null);

  const navigate = useNavigate(); // Hook para redirección

  const handleLogin = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombreUsuario: username,
          contrasena: password,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        localStorage.setItem("jwtToken", data.token);

        const decodedToken = jwtDecode(data.token);
        console.log("Token decodificado:", decodedToken);
        const userRole = decodedToken.role;

        if (userRole === "ADMIN") {
          navigate("/admin-dashboard");
        } else if (userRole === "USER") {
          navigate("/user-dashboard");
        } else {
          setMessage("No se pudo determinar el rol del usuario.");
        }
      } else {
        setMessage(data.message || "Error al iniciar sesión.");
      }
    } catch (error) {
      setMessage("No se pudo conectar al servidor.");
    }
  };

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">PROPSUITE</h1>
        <hr className="divider" />
        {message && <p className="message">{message}</p>}

        {!showLogin && !showRegister && (
          <div>
            <button onClick={() => setShowLogin(true)}>Iniciar Sesión</button>
            <button onClick={() => setShowRegister(true)}>Registrarse</button>
          </div>
        )}

        {showLogin && (
          <div>
            <input
              type="text"
              placeholder="Usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Ingresar</button>
            <button onClick={() => setShowLogin(false)}>Volver</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
