import React, { useState } from "react";
import "./UserDashboard.css";

function UsuarioDashboard() {
  const [showModal, setShowModal] = useState(null); // Controla qué modal mostrar
  const [modalData, setModalData] = useState(null); // Almacena datos para mostrar en el modal
  const [formData, setFormData] = useState({
    nuevoNombreUsuario: "",
    nombreUsuario: "",
    dni: "",
    nuevaContrasena: "",
    codigoEdificio: "",
    piso: "",
    numeroUnidad: "",
    ubicacion: "",
    descripcion: "",
    tipoReclamo: "",
    idReclamo: "",
    idImagen: "",
    archivo: null,
  });

  const cerrarSesion = () => {
    localStorage.removeItem("jwtToken"); // Elimina el token
    alert("Sesión cerrada. Redirigiendo a la página de inicio.");
    window.location.href = "http://localhost:3000/"; // Redirige a la página de inicio
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, archivo: e.target.files[0] });
  };

  const fetchData = async (url, method, body = null) => {
    try {
      const headers = {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
      };

      const options = { method, headers };
      if (body) options.body = JSON.stringify(body);

      const response = await fetch(url, options);

      if (response.ok) {
        return await response.json();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || "Error desconocido");
      }
    } catch (error) {
      console.error("Error:", error.message);
      alert(`Error: ${error.message}`);
    }
  };

  const actualizarNombreUsuario = async () => {
    await fetchData("http://localhost:8080/api/usuario/actualizar-nombre-usuario", "PUT", {
      nuevoNombreUsuario: formData.nuevoNombreUsuario,
    });
    alert("Nombre de usuario actualizado. Por favor, vuelve a iniciar sesión.");
    cerrarSesion();
  };

  const restablecerContrasena = async () => {
    await fetchData("http://localhost:8080/api/usuario/restablecerContrasena", "POST", {
      nombreUsuario: formData.nombreUsuario,
      dni: formData.dni,
      nuevaContrasena: formData.nuevaContrasena,
    });
    alert("Contraseña restablecida correctamente. Por favor, vuelve a iniciar sesión.");
    cerrarSesion();
  };

  const obtenerReclamoPorId = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/${formData.idReclamo}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };

  const obtenerReclamosPorEdificio = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/edificio/${formData.codigoEdificio}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };

  const obtenerReclamosPorUnidad = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/unidad/${formData.codigoEdificio}/${formData.piso}/${formData.numeroUnidad}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };

  return (
    <div className="user-dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <h1 className="dashboard-title">PROPSUITE</h1>
        <nav className="header-buttons">
          <div className="dropdown">
            <button className="nav-button">Usuario</button>
            <ul className="dropdown-menu">
              <li>
                <button
                  className="dropdown-item"
                  onClick={() => setShowModal("actualizarNombreUsuario")}
                >
                  Actualizar nombre de usuario
                </button>
              </li>
              <li>
                <button
                  className="dropdown-item"
                  onClick={() => setShowModal("restablecerContrasena")}
                >
                  Restablecer contraseña
                </button>
              </li>
              <li>
                <button className="dropdown-item" onClick={cerrarSesion}>
                  Cerrar sesión
                </button>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      {/* Bienvenida */}
      <div className="welcome-section">
        <h2 className="welcome-title">PROPSUITE</h2>
        <p className="welcome-subtitle">
          Bienvenidos a nuestro sistema, diseñado para hacer tu gestión fácil y dinámica.
        </p>
      </div>

      {/* Sección Reclamos */}
      <div className="section-container">
        <h2 className="section-title">Reclamos</h2>
        <button
          className="section-button"
          onClick={() => setShowModal("obtenerReclamoPorId")}
        >
          Ver Reclamo por ID
        </button>
        <button
          className="section-button"
          onClick={() => setShowModal("obtenerReclamosPorEdificio")}
        >
          Ver Reclamos por Edificio
        </button>
        <button
          className="section-button"
          onClick={() => setShowModal("obtenerReclamosPorUnidad")}
        >
          Ver Reclamos por Unidad
        </button>
      </div>

      {/* Modales */}
      {showModal === "actualizarNombreUsuario" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Actualizar Nombre de Usuario</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nuevo nombre de usuario"
              value={formData.nuevoNombreUsuario}
              onChange={(e) => setFormData({ ...formData, nuevoNombreUsuario: e.target.value })}
            />
            <button className="section-button" onClick={actualizarNombreUsuario}>
              Actualizar
            </button>
            <button className="section-button" onClick={() => setShowModal(null)}>
              Cerrar
            </button>
          </div>
        </div>
      )}

      {showModal === "restablecerContrasena" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Restablecer Contraseña</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nombre de usuario"
              value={formData.nombreUsuario}
              onChange={(e) => setFormData({ ...formData, nombreUsuario: e.target.value })}
            />
            <input
              className="modal-input"
              type="text"
              placeholder="DNI"
              value={formData.dni}
              onChange={(e) => setFormData({ ...formData, dni: e.target.value })}
            />
            <input
              className="modal-input"
              type="password"
              placeholder="Nueva contraseña"
              value={formData.nuevaContrasena}
              onChange={(e) => setFormData({ ...formData, nuevaContrasena: e.target.value })}
            />
            <button className="section-button" onClick={restablecerContrasena}>
              Restablecer
            </button>
            <button className="section-button" onClick={() => setShowModal(null)}>
              Cerrar
            </button>
          </div>
        </div>
      )}

      {showModal === "resultado" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Resultados</h2>
            <pre className="modal-data">{modalData}</pre>
            <button className="section-button" onClick={() => setShowModal(null)}>
              Cerrar
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default UsuarioDashboard;
