import React, { useState } from "react";
import "./UserDashboard.css";

function UsuarioDashboard() {
  const [showModal, setShowModal] = useState(null); // Controla qué modal mostrar
  const [formData, setFormData] = useState({
    nuevoNombreUsuario: "",
    nombreUsuario: "",
    dni: "",
    nuevaContrasena: "",
    codigoEdificio: "",
    piso: "",
    numeroUnidad: "",
    ubicacion: "",
    descripcion: "",
    tipoReclamo: "",
    idReclamo: "",
    archivo: null, // Para imágenes
  });

  const cerrarSesion = () => {
    localStorage.removeItem("jwtToken");
    alert("Sesión cerrada. Redirigiendo a la página de inicio.");
    window.location.href = "http://localhost:3000/";
  };

  const handleInputChange = (e) => {
    const { name, value, type, files } = e.target;
    setFormData({
      ...formData,
      [name]: type === "file" ? files[0] : value,
    });
  };

  const fetchData = async (url, method, body = null) => {
    try {
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": body instanceof FormData ? undefined : "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        },
        body: body instanceof FormData ? body : JSON.stringify(body),
      });

      if (response.ok) {
        const data = await response.json();
        alert("Operación realizada con éxito");
        return data;
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.message || "Error desconocido"}`);
      }
    } catch (e) {
      console.error("Error al conectar con el servidor:", e);
      alert("Error al conectar con el servidor");
    }
  };

  const realizarOperacion = async (endpoint, method, extraData = {}) => {
    const body =
      endpoint.includes("agregarImagen") || endpoint.includes("actualizarImagen")
        ? new FormData()
        : { ...formData, ...extraData };

    if (body instanceof FormData) {
      body.append("archivo", formData.archivo);
      body.append("idReclamo", formData.idReclamo);
    }

    await fetchData(`http://localhost:8080/api/${endpoint}`, method, body);
    setShowModal(null);
  };

  const modalContent = {
    actualizarNombreUsuario: (
      <>
        <h2>Actualizar Nombre de Usuario</h2>
        <input
          type="text"
          name="nuevoNombreUsuario"
          placeholder="Nuevo nombre de usuario"
          value={formData.nuevoNombreUsuario}
          onChange={handleInputChange}
        />
        <button
          className="section-button"
          onClick={() => realizarOperacion("usuario/actualizar-nombre-usuario", "PUT")}
        >
          Guardar
        </button>
      </>
    ),
    restablecerContrasena: (
      <>
        <h2>Restablecer Contraseña</h2>
        <input
          type="text"
          name="nombreUsuario"
          placeholder="Nombre de usuario"
          value={formData.nombreUsuario}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="dni"
          placeholder="DNI"
          value={formData.dni}
          onChange={handleInputChange}
        />
        <input
          type="password"
          name="nuevaContrasena"
          placeholder="Nueva contraseña"
          value={formData.nuevaContrasena}
          onChange={handleInputChange}
        />
        <button
          className="section-button"
          onClick={() => realizarOperacion("usuario/restablecerContrasena", "POST")}
        >
          Restablecer
        </button>
      </>
    ),
    agregarReclamoConUnidad: (
      <>
        <h2>Agregar Reclamo con Unidad</h2>
        <input
          type="text"
          name="codigoEdificio"
          placeholder="Código del edificio"
          value={formData.codigoEdificio}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="piso"
          placeholder="Piso"
          value={formData.piso}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="numeroUnidad"
          placeholder="Número de unidad"
          value={formData.numeroUnidad}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="ubicacion"
          placeholder="Ubicación"
          value={formData.ubicacion}
          onChange={handleInputChange}
        />
        <textarea
          name="descripcion"
          placeholder="Descripción del reclamo"
          value={formData.descripcion}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="tipoReclamo"
          placeholder="Tipo de reclamo"
          value={formData.tipoReclamo}
          onChange={handleInputChange}
        />
        <button
          className="section-button"
          onClick={() => realizarOperacion("reclamos/conUnidad", "POST")}
        >
          Guardar
        </button>
      </>
    ),
    agregarReclamoAreaComun: (
      <>
        <h2>Agregar Reclamo en Área Común</h2>
        <input
          type="text"
          name="codigoEdificio"
          placeholder="Código del edificio"
          value={formData.codigoEdificio}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="ubicacion"
          placeholder="Ubicación"
          value={formData.ubicacion}
          onChange={handleInputChange}
        />
        <textarea
          name="descripcion"
          placeholder="Descripción del reclamo"
          value={formData.descripcion}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="tipoReclamo"
          placeholder="Tipo de reclamo"
          value={formData.tipoReclamo}
          onChange={handleInputChange}
        />
        <button
          className="section-button"
          onClick={() => realizarOperacion("reclamos/sinUnidad", "POST")}
        >
          Guardar
        </button>
      </>
    ),
    // Otros modales para actualizar reclamo, agregar imágenes, etc.
  };

  return (
    <div className="user-dashboard">
      <header className="dashboard-header">
        <h1 className="dashboard-title">PROPSUITE</h1>
        <nav className="header-buttons">
          <div className="dropdown">
            <button className="nav-button">Usuario</button>
            <ul className="dropdown-menu">
              <li>
                <button onClick={() => setShowModal("actualizarNombreUsuario")}>
                  Actualizar nombre de usuario
                </button>
              </li>
              <li>
                <button onClick={() => setShowModal("restablecerContrasena")}>
                  Restablecer contraseña
                </button>
              </li>
              <li>
                <button onClick={cerrarSesion}>Cerrar sesión</button>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      {/* Sección modales */}
      {showModal && (
        <div className="modal">
          <div className="modal-content custom-modal">{modalContent[showModal]}</div>
          <button className="close-modal" onClick={() => setShowModal(null)}>
            Cerrar
          </button>
        </div>
      )}
    </div>
  );
}

export default UsuarioDashboard;
