import React, { useState } from "react";
import jwtDecode from "jwt-decode"; // Corrige la importación
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userRole, setUserRole] = useState(null); // Almacena el rol del usuario
  const [message, setMessage] = useState(null); // Almacena mensajes de éxito o error

  const handleLogin = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/auth/login", { // Usa la URL completa
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombreUsuario: username,
          contrasena: password,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        // Guardar token en localStorage
        localStorage.setItem("jwtToken", data.token);

        // Decodificar el token para extraer el rol
        const decodedToken = jwtDecode(data.token);
        setUserRole(decodedToken.role);

        // Mostrar mensaje de éxito
        setMessage("Inicio de sesión exitoso. Bienvenido!");

        // Resetear el formulario y ocultar login
        setUsername("");
        setPassword("");
        setShowLogin(false);
      } else {
        // Si la respuesta no es exitosa, muestra el mensaje de error
        setMessage(data.message || "Error al iniciar sesión");
      }
    } catch (error) {
      // Muestra cualquier error relacionado con la conexión o el servidor
      setMessage("No se pudo conectar al servidor.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("jwtToken");
    setUserRole(null);
    setMessage("Has cerrado sesión correctamente.");
    setShowLogin(true);
  };

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">PROPSUITE</h1>
        <hr className="divider" />
        {!showLogin ? (
          <>
            <h2 className="subtitle">¡Gestión de edificios al alcance de tus manos!</h2>
            {message && <p className="success-message">{message}</p>} {/* Mensaje de éxito */}
            <div className="button-container">
              <button className="custom-button" onClick={() => setShowLogin(true)}>
                Iniciar Sesión
              </button>
              <button className="custom-button">Registrarse</button>
              <button className="custom-button" onClick={handleLogout}>
                Cerrar Sesión
              </button>
            </div>
          </>
        ) : (
          <div className="form-horizontal">
            <div className="input-group">
              <label>Usuario</label>
              <input
                type="text"
                placeholder="Ingrese su usuario"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="input-group">
              <label>Contraseña</label>
              <input
                type="password"
                placeholder="Ingrese su contraseña"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="button-container">
              <button className="custom-button" onClick={handleLogin}>
                Iniciar Sesión
              </button>
              <button className="custom-button" onClick={() => setShowLogin(false)}>
                Volver
              </button>
            </div>
            {message && <p className="error-message">{message}</p>} {/* Mensaje de error */}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
