import React, { useState } from "react";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(''); // 'admin' o 'user'

  const handleLogin = (role) => {
    setIsLoggedIn(true);
    setUserRole(role); // Aquí configurarías el rol basado en la respuesta del backend
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserRole('');
  };

  return (
    <div className="welcome-container">
      <div className="welcome-content">
        <h1 className="main-title">PROPSUITE</h1>
        <hr className="divider" />
        {!isLoggedIn ? (
          <>
            <h2 className="subtitle">
              ¡Gestión de edificios al alcance de tus manos!
            </h2>
            <div className="button-container">
              <button className="custom-button" onClick={() => handleLogin('user')}>
                Login Usuario
              </button>
              <button className="custom-button" onClick={() => handleLogin('admin')}>
                Login Admin
              </button>
            </div>
          </>
        ) : (
          <>
            <h2 className="subtitle">
              {userRole === 'admin' ? 'Panel de Administrador' : 'Panel de Usuario'}
            </h2>
            <div className="button-container">
              <button className="custom-button" onClick={handleLogout}>
                Cerrar Sesión
              </button>
            </div>
            {userRole === 'admin' && (
              <p>Más opciones de administración disponibles aquí.</p>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default App;
