import React, { useState } from "react";
import "./UserDashboard.css";

function UsuarioDashboard() {
  const [showModal, setShowModal] = useState(null); // Controla qué modal mostrar
  const [formData, setFormData] = useState({
    nuevoNombreUsuario: "",
    nombreUsuario: "",
    dni: "",
    nuevaContrasena: "",
    codigoEdificio: "",
    piso: "",
    numeroUnidad: "",
    ubicacion: "",
    descripcion: "",
    tipoReclamo: "",
    idReclamo: "",
    idImagen: "",
    archivo: null,
  });

  const cerrarSesion = () => {
    localStorage.removeItem("jwtToken");
    alert("Sesión cerrada. Redirigiendo a la página de inicio.");
    window.location.href = "http://localhost:3000/";
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, archivo: e.target.files[0] });
  };

  const fetchData = async (url, method, body) => {
    try {
      const headers = {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
      };

      const options = {
        method,
        headers,
      };

      if (body) options.body = JSON.stringify(body);

      const response = await fetch(url, options);
      if (response.ok) {
        return await response.json();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || "Error desconocido");
      }
    } catch (error) {
      console.error("Error:", error.message);
      alert(`Error: ${error.message}`);
      throw error;
    }
  };

  const agregarReclamoConUnidad = async () => {
    const body = {
      codigoEdificio: formData.codigoEdificio,
      piso: formData.piso,
      numeroUnidad: formData.numeroUnidad,
      ubicacion: formData.ubicacion,
      descripcion: formData.descripcion,
      tipoReclamo: formData.tipoReclamo,
    };
    await fetchData("http://localhost:8080/api/reclamos/conUnidad", "POST", body);
    alert("Reclamo agregado con éxito.");
    setShowModal(null);
  };

  const agregarReclamoSinUnidad = async () => {
    const body = {
      codigoEdificio: formData.codigoEdificio,
      ubicacion: formData.ubicacion,
      descripcion: formData.descripcion,
      tipoReclamo: formData.tipoReclamo,
    };
    await fetchData("http://localhost:8080/api/reclamos/sinUnidad", "POST", body);
    alert("Reclamo agregado con éxito.");
    setShowModal(null);
  };

  const actualizarReclamo = async () => {
    const body = {
      idReclamo: formData.idReclamo,
      ubicacion: formData.ubicacion,
      descripcion: formData.descripcion,
    };
    await fetchData("http://localhost:8080/api/reclamos/actualizar", "PUT", body);
    alert("Reclamo actualizado con éxito.");
    setShowModal(null);
  };

  const modalActions = {
    agregarReclamoConUnidad: {
      title: "Agregar Reclamo a Unidad",
      inputs: [
        { name: "codigoEdificio", placeholder: "Código del edificio" },
        { name: "piso", placeholder: "Piso" },
        { name: "numeroUnidad", placeholder: "Número de la unidad" },
        { name: "ubicacion", placeholder: "Ubicación" },
        { name: "descripcion", placeholder: "Descripción" },
        { name: "tipoReclamo", placeholder: "Tipo de reclamo" },
      ],
      action: agregarReclamoConUnidad,
    },
    agregarReclamoSinUnidad: {
      title: "Agregar Reclamo a Área Común",
      inputs: [
        { name: "codigoEdificio", placeholder: "Código del edificio" },
        { name: "ubicacion", placeholder: "Ubicación" },
        { name: "descripcion", placeholder: "Descripción" },
        { name: "tipoReclamo", placeholder: "Tipo de reclamo" },
      ],
      action: agregarReclamoSinUnidad,
    },
    actualizarReclamo: {
      title: "Actualizar Reclamo",
      inputs: [
        { name: "idReclamo", placeholder: "ID del reclamo" },
        { name: "ubicacion", placeholder: "Ubicación" },
        { name: "descripcion", placeholder: "Descripción" },
      ],
      action: actualizarReclamo,
    },
  };

  return (
    <div className="user-dashboard">
      <header className="dashboard-header">
        <h1 className="dashboard-title">PROPSUITE</h1>
        <nav className="header-buttons">
          <div className="dropdown">
            <button className="nav-button">Usuario</button>
            <ul className="dropdown-menu">
              <li>
                <button onClick={cerrarSesion}>Cerrar sesión</button>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      <div className="section-container">
        <h2 className="section-title">Reclamos</h2>
        {Object.keys(modalActions).map((key) => (
          <button
            key={key}
            className="section-button"
            onClick={() => setShowModal(key)}
          >
            {modalActions[key].title}
          </button>
        ))}
      </div>

      {showModal && (
        <Modal
          title={modalActions[showModal].title}
          inputs={modalActions[showModal].inputs}
          onInputChange={handleInputChange}
          onSubmit={modalActions[showModal].action}
          onClose={() => setShowModal(null)}
        />
      )}
    </div>
  );
}

function Modal({ title, inputs, onInputChange, onSubmit, onClose }) {
  return (
    <div className="modal">
      <div className="modal-content custom-modal">
        <h2 className="modal-title">{title}</h2>
        {inputs.map((input) => (
          <input
            key={input.name}
            className="modal-input"
            type="text"
            name={input.name}
            placeholder={input.placeholder}
            onChange={onInputChange}
          />
        ))}
        <div className="modal-buttons">
          <button className="section-button" onClick={onSubmit}>
            Guardar
          </button>
          <button className="section-button" onClick={onClose}>
            Cancelar
          </button>
        </div>
      </div>
    </div>
  );
}

export default UsuarioDashboard;
