import React, { useState } from "react";
import "./UserDashboard.css";

function UsuarioDashboard() {
  const [showModal, setShowModal] = useState(null);
  const [nuevoNombreUsuario, setNuevoNombreUsuario] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");
  const [dni, setDni] = useState("");
  const [nuevaContrasena, setNuevaContrasena] = useState("");
  const [idReclamo, setIdReclamo] = useState("");
  const [codigoEdificio, setCodigoEdificio] = useState("");
  const [piso, setPiso] = useState("");
  const [numero, setNumero] = useState("");

  const actualizarNombreUsuario = async () => {
    try {
      console.log("Iniciando solicitud para actualizar nombre de usuario...");
      const response = await fetch("http://localhost:8080/api/usuario/actualizar-nombre-usuario", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        },
        body: JSON.stringify({ nuevoNombreUsuario }),
      });

      console.log("Respuesta del servidor:", response);

      if (response.ok) {
        alert("Nombre de usuario actualizado con éxito. Por favor, vuelve a iniciar sesión.");
        cerrarSesion();
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.message || "Error desconocido"}`);
      }
    } catch (e) {
      console.error("Error al conectar con el servidor:", e);
      alert("Error al conectar con el servidor");
    }
  };

  const restablecerContrasena = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/usuario/restablecerContrasena", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombreUsuario,
          dni,
          nuevaContrasena,
        }),
      });

      if (response.ok) {
        alert("Contraseña restablecida correctamente. Por favor, vuelve a iniciar sesión.");
        cerrarSesion();
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.message || "Error desconocido"}`);
      }
    } catch (e) {
      console.error("Error al conectar con el servidor:", e);
      alert("Error al conectar con el servidor");
    }
  };

  const cerrarSesion = () => {
    localStorage.removeItem("jwtToken");
    alert("Sesión cerrada. Redirigiendo a la página de inicio.");
    window.location.href = "http://localhost:3000/";
  };

  const obtenerReclamoPorId = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/reclamos/${idReclamo}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Reclamo encontrado: ${JSON.stringify(data)}`);
      } else {
        throw new Error(data.message || "Error desconocido");
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  const obtenerReclamosPorEdificio = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/reclamos/edificio/${codigoEdificio}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Reclamos del edificio ${codigoEdificio}: ${JSON.stringify(data)}`);
      } else {
        throw new Error(data.message || "Error desconocido");
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  const obtenerReclamosPorUnidad = async () => {
    try {
      const response = await fetch(`http://localhost:8080/api/reclamos/unidad/${codigoEdificio}/${piso}/${numero}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        }
      });

      const data = await response.json();
      if (response.ok) {
        alert(`Reclamos de la unidad ${piso}-${numero} en el edificio ${codigoEdificio}: ${JSON.stringify(data)}`);
      } else {
        throw new Error(data.message || "Error desconocido");
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  return (
    <div className="user-dashboard">
      <header className="dashboard-header">
        <h1 className="dashboard-title">PROPSUITE</h1>
        <nav className="header-buttons">
          <div className="dropdown">
            <button className="nav-button">Usuario</button>
            <ul className="dropdown-menu">
              <li><button className="dropdown-item" onClick={() => setShowModal("actualizarNombreUsuario")}>Actualizar nombre de usuario</button></li>
              <li><button className="dropdown-item" onClick={() => setShowModal("restablecerContrasena")}>Restablecer contraseña</button></li>
              <li><button className="dropdown-item" onClick={cerrarSesion}>Cerrar sesión</button></li>
            </ul>
          </div>
        </nav>
      </header>
      <div className="welcome-section">
        <h2 className="welcome-title">PROPSUITE</h2>
        <p className="welcome-subtitle">Bienvenidos a nuestro sistema, diseñado para hacer tu gestión fácil y dinámica.</p>
      </div>
      <div className="section-container">
        <h2 className="section-title">Reclamos</h2>
        <button className="section-button" onClick={() => setShowModal("obtenerReclamoPorId")}>Ver Reclamo por ID</button>
        <button className="section-button" onClick={() => setShowModal("obtenerReclamosPorEdificio")}>Ver Reclamos por Edificio</button>
        <button className="section-button" onClick={() => setShowModal("obtenerReclamosPorUnidad")}>Ver Reclamos por Unidad</button>
      </div>
      <div className="section-container">
        <h2 className="section-title">Unidades</h2>
        <button className="section-button">Mis Unidades</button>
      </div>

      {showModal === "actualizarNombreUsuario" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Actualizar Nombre de Usuario</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nuevo nombre de usuario"
              value={nuevoNombreUsuario}
              onChange={(e) => setNuevoNombreUsuario(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={actualizarNombreUsuario}>Guardar</button>
              <button className="section-button" onClick={() => setShowModal(null)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {showModal === "restablecerContrasena" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Restablecer Contraseña</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nombre de usuario"
              value={nombreUsuario}
              onChange={(e) => setNombreUsuario(e.target.value)}
            />
            <input
              className="modal-input"
              type="text"
              placeholder="Documento: Ejemplo: 'DNI45286575'"
              value={dni}
              onChange={(e) => setDni(e.target.value)}
            />
            <input
              className="modal-input"
              type="password"
              placeholder="Nueva contraseña"
              value={nuevaContrasena}
              onChange={(e) => setNuevaContrasena(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={restablecerContrasena}>Restablecer</button>
              <button className="section-button" onClick={() => setShowModal(null)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {showModal === "obtenerReclamoPorId" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Ver Reclamo por ID</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="ID del Reclamo"
              value={idReclamo}
              onChange={(e) => setIdReclamo(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={obtenerReclamoPorId}>Buscar</button>
              <button className="section-button" onClick={() => setShowModal(null)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {showModal === "obtenerReclamosPorEdificio" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Ver Reclamos por Edificio</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Código del Edificio"
              value={codigoEdificio}
              onChange={(e) => setCodigoEdificio(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={obtenerReclamosPorEdificio}>Buscar</button>
              <button className="section-button" onClick={() => setShowModal(null)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}

      {showModal === "obtenerReclamosPorUnidad" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Ver Reclamos por Unidad</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Código del Edificio"
              value={codigoEdificio}
              onChange={(e) => setCodigoEdificio(e.target.value)}
            />
            <input
              className="modal-input"
              type="text"
              placeholder="Piso"
              value={piso}
              onChange={(e) => setPiso(e.target.value)}
            />
            <input
              className="modal-input"
              type="text"
              placeholder="Número de Unidad"
              value={numero}
              onChange={(e) => setNumero(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={obtenerReclamosPorUnidad}>Buscar</button>
              <button className="section-button" onClick={() => setShowModal(null)}>Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UsuarioDashboard;
