import React, { useState } from "react";
import { jwtDecode } from "jwt-decode";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(true); // Mostrar el formulario de login por defecto
  const [username, setUsername] = useState(""); // Estado para nombre de usuario
  const [password, setPassword] = useState(""); // Estado para la contraseña
  const [userRole, setUserRole] = useState(null); // Estado para almacenar el rol del usuario
  const [errorMessage, setErrorMessage] = useState(null); // Estado para mensajes de error

  // Manejo del inicio de sesión
  const handleLogin = async (e) => {
    e.preventDefault(); // Prevenir la recarga de la página

    try {
      const response = await fetch("http://localhost:8080/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombreUsuario: username,
          contrasena: password,
        }),
      });

      if (response.ok) {
        const data = await response.json();

        // Guardar token en localStorage
        localStorage.setItem("jwtToken", data.token);

        // Decodificar el token para extraer el rol del usuario
        const decodedToken = jwtDecode(data.token);
        setUserRole(decodedToken.role);

        // Resetear mensajes de error y ocultar el formulario de login
        setErrorMessage(null);
        setShowLogin(false);
      } else {
        const errorData = await response.json();
        setErrorMessage(errorData.message || "Error al iniciar sesión");
      }
    } catch (error) {
      setErrorMessage("No se pudo conectar al servidor.");
    }
  };

  return (
    <div className="app-container">
      {showLogin ? (
        <div className="login-form">
          <h2>Iniciar Sesión</h2>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <form onSubmit={handleLogin}>
            <div>
              <label>Usuario:</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Ingrese su usuario"
                required
              />
            </div>
            <div>
              <label>Contraseña:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Ingrese su contraseña"
                required
              />
            </div>
            <button type="submit">Iniciar Sesión</button>
          </form>
        </div>
      ) : (
        <div>
          <h2>Bienvenido</h2>
          <p>Rol del usuario: {userRole}</p>
        </div>
      )}
    </div>
  );
}

export default App;
