      method: "GET",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
      },
    });

    if (response.ok) {
      const tipo = await response.json();
      setModalContent(tipo);
      setShowModal("obtenerTipoReclamoPorId");
    } else {
