import React, { useState } from "react";
import "./UserDashboard.css";

function UsuarioDashboard() {
  const [showModal, setShowModal] = useState(null); // Controla qué modal mostrar
  const [nuevoNombreUsuario, setNuevoNombreUsuario] = useState("");
  const [nombreUsuario, setNombreUsuario] = useState("");
  const [dni, setDni] = useState("");
  const [nuevaContrasena, setNuevaContrasena] = useState("");

  const actualizarNombreUsuario = async () => {
    try {
      console.log("Iniciando solicitud para actualizar nombre de usuario...");
      const response = await fetch("http://localhost:8080/api/usuario/actualizar-nombre-usuario", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        },
        body: JSON.stringify({ nuevoNombreUsuario }),
      });

      console.log("Respuesta del servidor:", response);

      if (response.ok) {
        alert("Nombre de usuario actualizado con éxito. Por favor, vuelve a iniciar sesión.");
        cerrarSesion(); // Cierra la sesión y redirige
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.message || "Error desconocido"}`);
      }
    } catch (e) {
      console.error("Error al conectar con el servidor:", e);
      alert("Error al conectar con el servidor");
    }
  };

  const restablecerContrasena = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/usuario/restablecerContrasena", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombreUsuario,
          dni,
          nuevaContrasena,
        }),
      });

      if (response.ok) {
        alert("Contraseña restablecida correctamente. Por favor, vuelve a iniciar sesión.");
        cerrarSesion(); // Cierra la sesión y redirige
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.message || "Error desconocido"}`);
      }
    } catch (e) {
      console.error("Error al conectar con el servidor:", e);
      alert("Error al conectar con el servidor");
    }
  };

  const cerrarSesion = () => {
    localStorage.removeItem("jwtToken"); // Elimina el token
    alert("Sesión cerrada. Redirigiendo a la página de inicio.");
    window.location.href = "http://localhost:3000/"; // Redirige a la página de inicio
  };

  const obtenerReclamoPorId = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/${formData.idReclamo}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };

  const obtenerReclamosPorEdificio = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/edificio/${formData.codigoEdificio}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };

  const obtenerReclamosPorUnidad = async () => {
    const data = await fetchData(
      `http://localhost:8080/api/reclamos/unidad/${formData.codigoEdificio}/${formData.piso}/${formData.numeroUnidad}`,
      "GET"
    );
    setModalData(JSON.stringify(data, null, 2));
    setShowModal("resultado");
  };







  return (
    <div className="user-dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <h1 className="dashboard-title">PROPSUITE</h1>
        <nav className="header-buttons">
          <div className="dropdown">
            <button className="nav-button">Usuario</button>
            <ul className="dropdown-menu">
              <li>
                <button
                  className="dropdown-item"
                  onClick={() => setShowModal("actualizarNombreUsuario")}
                >
                  Actualizar nombre de usuario
                </button>
              </li>
              <li>
                <button
                  className="dropdown-item"
                  onClick={() => setShowModal("restablecerContrasena")}
                >
                  Restablecer contraseña
                </button>
              </li>
              <li>
                <button className="dropdown-item" onClick={cerrarSesion}>
                  Cerrar sesión
                </button>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      {/* Bienvenida */}
      <div className="welcome-section">
        <h2 className="welcome-title">PROPSUITE</h2>
        <p className="welcome-subtitle">
          Bienvenidos a nuestro sistema, diseñado para hacer tu gestión fácil y dinámica.
        </p>
      </div>

      {/* Sección Reclamos */}
      <div className="section-container">
        <h2 className="section-title">Reclamos</h2>
        <button className="section-button">Agregar Reclamo a Unidad</button>
        <button className="section-button">Agregar Reclamo a Area Comun</button>
        <button className="section-button">Actualizar Reclamo </button>
        <button className="section-button">Agregar Imagen al Reclamo </button>
        <button className="section-button">Actualizar Imagen del Reclamo</button>
        <button className="section-button">Eliminar Imagen del Reclamo</button>
        <button className="section-button">Ver Imagen por reclamo</button>
        <button className="section-button">Ver Mis Reclamos</button>
        <button className="section-button">Ver Reclamo por ID</button>

      </div>

      {/* Sección Unidades */}
      <div className="section-container">
        <h2 className="section-title">Unidades</h2>
        <button className="section-button">Mis Unidades</button>
      </div>

      {/* Modales */}
      {showModal === "actualizarNombreUsuario" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Actualizar Nombre de Usuario</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nuevo nombre de usuario"
              value={nuevoNombreUsuario}
              onChange={(e) => setNuevoNombreUsuario(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={actualizarNombreUsuario}>
                Guardar
              </button>
              <button className="section-button" onClick={() => setShowModal(null)}>
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}

      {showModal === "restablecerContrasena" && (
        <div className="modal">
          <div className="modal-content custom-modal">
            <h2 className="modal-title">Restablecer Contraseña</h2>
            <input
              className="modal-input"
              type="text"
              placeholder="Nombre de usuario"
              value={nombreUsuario}
              onChange={(e) => setNombreUsuario(e.target.value)}
            />
            <input
              className="modal-input"
              type="text"
              placeholder="Documento: Ejemplo: 'DNI45286575'"
              value={dni}
              onChange={(e) => setDni(e.target.value)}
            />
            <input
              className="modal-input"
              type="password"
              placeholder="Nueva contraseña"
              value={nuevaContrasena}
              onChange={(e) => setNuevaContrasena(e.target.value)}
            />
            <div className="modal-buttons">
              <button className="section-button" onClick={restablecerContrasena}>
                Restablecer
              </button>
              <button className="section-button" onClick={() => setShowModal(null)}>
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UsuarioDashboard;
